$(function(){
  $('.slides').slick({
    autoplay: true,
    autoplaySpeed: 5000,
    dots: true,
    prevArrow: '<button class="prev-arrow"></button>',
    nextArrow: '<button class="next-arrow"></button>',
    dotsClass: 'slide-dots',
  });
  $('.box__slider').slick({
    autoplay: true,
    autoplaySpeed: 5000,
    arrows: true,
    slidesToShow:3,
    // appendArrows: $('prev-arrow2')
    prevArrow:'<div class="prev-arrow2"></div>',
    nextArrow:'<div class="next-arrow2"></div>'
  });
});

$(function(){
  $('.tabs li').click(function(){
    var index = $('.tabs li').index(this);
    $('.tabs li img').each(function(){
      var file = $(this).attr('src').replace(/_on/g, '_off');
      $(this).attr('src', file);
    });
    var file2 = $(this).children('img').attr('src').replace(/_off/g, '_on');
    $(this).children('img').attr('src', file2);
  });
});

$(function(){
  $('.tabs li').on('click', function(){
    $('.tabs li').removeClass('active');
    $(this).addClass('active');
    $('.tab__inner').removeClass('show');
    var index = $(this).index();
    $('.tab__inner').eq(index).addClass('show');
  });
})

