$(function(){
  $('.slides').slick({
    autoplay: true,
    autoplaySpeed: 5000,
    dots: true,
    prevArrow: '<button class="prev-arrow"></button>',
    nextArrow: '<button class="next-arrow"></button>',
    dotsClass: 'slide-dots',
  });
  $('.box__slider').slick({
    autoplay: true,
    autoplaySpeed: 5000,
    arrows: true,
    slidesToShow:3,
    // appendArrows: $('prev-arrow2')
    prevArrow:'<div class="prev-arrow2"></div>',
    nextArrow:'<div class="next-arrow2"></div>'
  });
});

// content2タブ画像切り替え
$(function(){
  $('.tabs li').click(function(){
    var index = $('.tabs li').index(this);
    $('.tabs li img').each(function(){
      var file = $(this).attr('src').replace(/_on/g, '_off');
      $(this).attr('src', file);
    });
    var file2 = $(this).children('img').attr('src').replace(/_off/g, '_on');
    $(this).children('img').attr('src', file2);
  });
});

// content2タブ内要素切り替え
$(function(){
  $('.tabs li').on('click', function(){
    $('.tabs li').removeClass('active');
    $(this).addClass('active');
    $('.tab__inner').removeClass('show');
    var index = $(this).index();
    $('.tab__inner').eq(index).addClass('show');
  });
});

// content2背景画像スクロール
$(function(){
  $(window).on('scroll', function(){
    var scrollTop = $(window).scrollTop();
    var scPosition = scrollTop / 2;
    if(scPosition){
      $('.content2').css('background-position', '50% -' + scPosition + 'px');
    }
  });
});

// トップへ戻るボタン
$(function(){
  var backToTop = $('.moveToTop');
  backToTop.hide();
  $(window).scroll(function(){
    if ($(this).scrollTop() > 100){
      backToTop.fadeIn();
    } else {
      backToTop.fadeOut();
    }
  });
  backToTop.click(function(){
    $('body,html').animate({
      scrollTop: 0
    }, 500);
    return false;
  });
});


$(function(){
  $(window).on('load scroll', function(){
    var fixed = $('.header').offset().top;
    var footer = $('.footer').offset().top-30;
    var windowScrollTop = $(window).scrollTop();
    var footerArea = windowScrollTop > footer;
    var beforeFixedArea = windowScrollTop < fixed;
    var fixedArea = windowScrollTop > fixed;
  // var distance = 0;
  // $(window).scroll(function(){
  //   distance = $(this).scrollTop();
    if (beforeFixedArea){
      $('.moveToTop').removeClass('fixed');
    } else if (fixedArea){
      $('.moveToTop').addClass('fixed');
    }
    if (footerArea){
      $('.moveToTop').removeClass('fixed');
      $('.moveToTop').addClass('static');
    }
    // if (scrollEnd <= distance){
    //   $('.moveToTop').addClass('static');
    // } else {
    //   $('.moveToTop').removeClass('static');
    // }
  });
});

